// search function
function dealerSearch() {
  // clear old results or initialize map
  if (loaded) {
    clearLocations();
  } else {
    $('#results').show();
    loaded = load();
  }

  // get input
  var searchInput = $('#dealer-search').val().toLowerCase();

  // check for state/abbreviation
  // TODO check for country input also
  if (states[searchInput]) {
    findByState(states[searchInput]);
  } else if (_.invert(states)[searchInput]) {
    findByState(searchInput);
  } else {
    // geocode address data into lat/lng
    var geocoder = new google.maps.Geocoder();
    geocoder.geocode({address: searchInput}, function(results, status)
    {
      if (status == google.maps.GeocoderStatus.OK) {
        // search locations near location
        var response = searchLocationsNear(results[0].geometry.location, searchInput);
        // show pins on map
        processResults(response);
      } else {
        alert(searchInput + ' not found');
      }
    });
  }
}

